var state = msg.payload.HBW_A1_s_state
var type = msg.payload.HBW_A1_s_type
var id = msg.payload.HBW_A1_s_id

var local=global.get('HBW_Rack_state') || {};
if (local.A1_color===undefined)//test exists
    {
    local.A1_color="";
    }

switch(state)
{
    case "RAW":
        msg.label = "<i class='fa fa-square fa-3x'></i>";
        break;
    case "PROCESSED":
        msg.label = "<i class='fa fa-circle fa-3x'></i>";
        break;
    default:
        // label lschen
        msg.label = "";
} 

switch(type)
{
    case "WHITE":
        msg.TXT_color = "white";
        local.A1_color = "white";    
        global.set('HBW_Rack_state',local);
        break;

    case "RED":
        msg.TXT_color = "red";
        local.A1_color = "red";    
        global.set('HBW_Rack_state',local);
        break;

    case "BLUE":
        msg.TXT_color = "blue";
        local.A1_color = "blue";    
        global.set('HBW_Rack_state',local);
        break;

    default:
        // Farbe auf normal setzen
        msg.TXT_color = "white";
        local.A1_color = "";    
        global.set('HBW_Rack_state',local);

} 


switch(id)
{
    case "":
        msg.BG_colour = "white";
        msg.TXT_colour = "white";
        msg.label = "";
        break;

    case "0":
        msg.BG_colour = "lightgray";
        msg.TXT_color = "lightgray";
        msg.label = "";
        break;

    default:
        // Farbe auf normal setzen
        msg.BG_colour = "lightgray";
} 



return {
    "topic": msg.topic,
    "payload": {
        "topic": (msg.topic),
        "label": (msg.label),
        "BG_color":(msg.BG_colour),
        "TXT_color":(msg.TXT_color),

    }
};

